import React from 'react'
import styled from 'styled-components'
export const Cursor = ({
  x,
  y,
  color,
  noRing,
  mixBlend,
  dotSize,
  ringSize,
  transitionTime
}) => {



  return (
    <div>
      <CursorDot
        color={color}
        mixBlend={mixBlend}
        dotSize={dotSize}
        x={x}
        y={y}
      />
      <CursorRing
        noRing={noRing}
        color={color}
        mixBlend={mixBlend}
        transitionTime={transitionTime}
        ringSize={ringSize}
        x={x}
        y={y}
      />
    </div>
  )
}

const CursorDot = styled.div.attrs((props) => ({
  style: {
    transform: `translate(${props.x}px, ${props.y}px)`
  }
}))`
  width: ${(props) => (props.dotSize ? props.dotSize + 'px' : '8px')};
  height: ${(props) => (props.dotSize ? props.dotSize + 'px' : '8px')};
  position: absolute;
  transform: translate(-50%, -50%);
  background: ${(props) => (props.color ? props.color : 'black')};
  z-index: 999;
  border-radius: 100%;
  pointer-events: none;
  overflow: auto;
  mix-blend-mode: ${(props) => (props.mixBlend ? 'difference' : 'none')};
`
const CursorRing = styled.div.attrs((props) => ({
  style: {
    transform: `translate(${props.x - props.ringSize / 2.25}px, ${
      props.y - props.ringSize / 2.25
    }px)`
  }
}))`
  width: ${(props) => (props.ringSize ? props.ringSize + 'px' : '36px')};
  height: ${(props) => (props.ringSize ? props.ringSize + 'px' : '36px')};
  border-radius: 100%;
  border: ${(props) =>
    props.color ? `2px solid ${props.color}` : '2px solid black'};
  transition: ${(props) =>
    props.transitionTime
      ? `transform ${props.transitionTime}ms ease-out`
      : 'transform 0.1s ease-out'};
  pointer-events: none;
  transform: translate(-50%, -50%);
  z-index: 999;
  position: absolute;
  mix-blend-mode: ${(props) => (props.mixBlend ? 'difference' : 'none')};
  display: ${(props) => (props.noRing ? 'none' : 'block')};
`

import React, { useState, useEffect } from 'react'
import styled, {createGlobalStyle} from 'styled-components'
import { Cursor } from './cursor'

const toMatch = [
    /Android/i,
    /webOS/i,
    /iPhone/i,
    /iPad/i,
    /iPod/i,
    /BlackBerry/i,
    /Windows Phone/i
];

const detectMob = (match) => {
    return match.some((match) => {
        return navigator.userAgent.match(match);
    });
}

export const CursorProvider = ({ children, color, noRing, ringSize, transitionTime }) => {
    useEffect(() => {
        detectMob(toMatch) ? setVisibility(false) : setVisibility(true)
    })

    const [cursor, setCursor] = useState({x: 0, y: 0})
    const [visibility, setVisibility] = useState(true)

    return (
    <CursorWrapper onMouseMove={(e) => setCursor({x: e.pageX, y: e.pageY})}>
        <GlobalStyle/>
        {visibility ? 
            <Cursor 
                color={color} 
                noRing={noRing} 
                ringSize={ringSize}
                transitionTime={transitionTime}
                x={cursor.x} 
                y={cursor.y}/> : 
            null}
        {children}
    </CursorWrapper>)
}

const CursorWrapper = styled.div`
    width: 100%;
    height: 100%;
`
const GlobalStyle = createGlobalStyle`
* {
    cursor: none !important;
}
`
